import React, { useState } from "react";
import { useEffect } from "react";

const App = () => {
    const [inputList, setInputList] = useState('');
    const [Items, setItems] = useState([]);

    const displayItems = () =>{
        if(!inputList){
            alert("Please add an item!")
        }else{
            setItems([...Items, inputList]);
            setInputList('');
        }
       
    }

    const deleteItem = (id) =>{
        const updatedItems = Items.filter((elem, index)=>{
            return index !==id;
        })
        setItems(updatedItems);
    }

    const RemoveAll = () =>{
        setItems([]);
    }

    useEffect(() => {
        localStorage.setItem(Lists, (Items))
    }, [Items]);

    return(
        <>
        <div className="main-div">
            <div className="center-div">
                <br/>
                <h1>ToDo-List</h1>
                <br />
                <input type="text" placeholder="Add an Item" value={inputList}  onChange={(e) => setInputList (e.target.value)} />
                <button className="add-btn" title="Add Item" onClick={displayItems}>+</button>
                <ol>
                    {
                        Items.map((itemsVal, index) =>{
                            return (
                            <div className="list" key={index}>
                                <i className="fa fa-times" aria-hidden="true" title="Delete Item" onClick={(e) => deleteItem(index)}></i>
                                <li>{itemsVal}</li>
                            </div>
                            )
                        })
                    } 
                </ol>
                <button className="clear-button" title="Clear All" onClick={RemoveAll}>Clear All</button>
            </div>
        </div>
        </>
    )
}

export default App;
