import React, { useState } from "react";
import { useEffect } from "react";

const App = () => {

    const getLocalItems = () => {
        let list = localStorage.getItem("Lists");

        if(list){
            return JSON.parse(localStorage.getItem("Lists"))
        }else{
            return [];
        }
    }

    const [inputList, setInputList] = useState('');
    const [Items, setItems] = useState(getLocalItems());

    useEffect(() => {
        localStorage.setItem("Lists", JSON.stringify(Items))
    }, [Items]);

   

    const displayItems = () =>{
        if(!inputList){
            alert("Please add an item!")
        }else{
            const allInputData = {id:new Date().getTime().toString(), name:inputList}
            setItems([...Items, allInputData]);
            setInputList('');
        }
       
    }

    const deleteItem = (index) =>{
        const updatedItems = Items.filter((elem)=>{
            return index !==id;
        })
        setItems(updatedItems);
    }

    const RemoveAll = () =>{
        setItems([]);
    }

    


    return(
        <>
        <div className="main-div">
            <div className="center-div">
                <br/>
                <h1>ToDo-List</h1>
                <br />
                <input type="text" placeholder="Add an Item" value={inputList}  onChange={(e) => setInputList (e.target.value)} />
                <button className="add-btn" title="Add Item" onClick={displayItems}>+</button>
                <ol>
                    {
                        Items.map((itemsVal) =>{
                            return (
                            <div className="list" key={itemsVal.id}>
                                <i className="fa fa-times" aria-hidden="true" title="Delete Item" onClick={(e) => deleteItem(itemsVal.id)}></i>
                                <li>{itemsVal.name}</li>
                            </div>
                            )
                        })
                    } 
                </ol>
                <button className="clear-button" title="Clear All" onClick={RemoveAll}>Clear All</button>
            </div>
        </div>
        </>
    )
}

export default App;
