import React, { useState } from "react";
import List from "./List"

const App = () => {
    const [inputList, setInputList] = useState('');
    const [Items, setItems] = useState([]);

    const itemEvent = (event) =>{
        setInputList(event.target.value);
    }

    const displayItems = () =>{
        setItems((oldItems) => {
            return [...oldItems, inputList]
        });
        setInputList('');
    }

    const deleteItem = (id) =>{
      return  setItems((oldItems) =>{
           return oldItems.filter((arrElem, index) =>{
                return index !==id;
           })
        })
    }

    const RemoveAll = () =>{
        setItems([]);
    }


    return(
        <>
        <div className="main-div">
            <div className="center-div">
                <br/>
                <h1>ToDo-List</h1>
                <br />
                <input type="text" placeholder="Add an Item" value={inputList}  onChange={setInputList } />
                <button className="add-btn" title="Add Item" onClick={displayItems}>+</button>
                <ol>
                    {
                        Items.map((itemsVal, index) =>{
                            return <List 
                            text={itemsVal}
                            key={index}
                            id = {index}
                            onSelect={deleteItem}/>
                        })
                    }
                </ol>
                <button className="clear-button" title="Clear All" onClick={RemoveAll}>Clear All</button>
            </div>
        </div>
        </>
    )
}

export default App;
